        $this->insert('{{%diandi_website_article}}',['id'=>'63','bloc_id'=>'27','store_id'=>'75','ishot'=>'0','pcate'=>'17','ccate'=>'0','template'=>'11','title'=>'西北大学智慧教室网络信息系统建设项目','description'=>'1','content'=>'<p>	大学智慧教室网络信息系统建设项目建设范围包含5号教学楼、6号教学楼共计86间教室。本项目建设运用物联网的先进技术，通过身份识别技术，将学生的到课、迟到、早退等信息完整的记录下来以实现自动考勤；通过智慧教室的各种摄像、传感设备记录教师的出勤情况，实现自动控制灯光、空调、通风换气等，及时发现问题，并保存证据，作为处理依据材料；通过远程监控，实现远距离听课、评课等活动；实现让学生更好得去学习、让老师更轻松的去授课的建设目标。</p><p class=\"ql-align-center\"><span style=\"color: rgb(0, 0, 0);\">系统建设施工现场图</span></p><p><img src=\"https://dev.hopesfire.com/attachment/202109/30/e009495f-60dc-3143-a3e9-94e2ce5e6ff8.jpg\"><img src=\"https://dev.hopesfire.com/attachment/202109/30/64eaff10-403a-332e-b4a9-084d55711428.jpg\"><img src=\"https://dev.hopesfire.com/attachment/202109/30/e982d0f8-4e81-3451-b663-fe3f5a555af6.jpg\"><img src=\"https://dev.hopesfire.com/attachment/202109/30/1f229bc0-919b-3d1a-b98a-61738baf7114.jpg\"></p>','thumb'=>'https://dev.hopesfire.com/attachment/202109/30/ec14276c-154a-3015-b3ec-d806f2a0d825.jpg','source'=>'1','author'=>'1','displayorder'=>'1','linkurl'=>'1','icon'=>'','is_top'=>'-1','type'=>'xmal','create_time'=>'1632974873','update_time'=>'1632974873']);
